$(document).ready((function(){$.get("https://students-api.up.railway.app/movies",(function(n){const e=document.getElementById("seccion-tarjetas");e.innerHTML="",n.forEach((n=>{const t=document.createElement("div");t.classList.add("tarjetas"),t.innerHTML=`\n            <h2>${n.title}</h2>\n            <p>Direction: ${n.director}.</p>\n            <p>Year: ${n.year}</p>\n            <p>Duration: ${n.duration}.</p>\n            <p>Genre: ${n.genre.join(", ")}.</p>\n            <p>Rate: ${n.rate}‚≠ê</p>\n            <img src="${n.poster}" alt="${n.title}">\n        `,e.appendChild(t)}))})).fail((function(){console.log("Hubo un error")}))}));